const $addToppingBtn=document.querySelector("#add-topping"),$pizzaForm=document.querySelector("#pizza-form"),$customToppingsList=document.querySelector("#custom-toppings-list"),handleAddTopping=e=>{e.preventDefault();const t=document.querySelector("#new-topping").value;if(!t)return!1;const n=document.createElement("input");n.type="checkbox",n.name="topping",n.value=t,n.id=t.toLowerCase().split(" ").join("-");const o=document.createElement("label");o.textContent=t,o.htmlFor=t.toLowerCase().split(" ").join("-");const i=document.createElement("div");i.appendChild(n),i.appendChild(o),$customToppingsList.appendChild(i),t.value=""},handlePizzaSubmit=e=>{e.preventDefault();const t=$pizzaForm.querySelector("#pizza-name").value,n=$pizzaForm.querySelector("#created-by").value,o=($pizzaForm.querySelector("#pizza-size").value,[...$pizzaForm.querySelectorAll("[name=topping]:checked")].map(e=>e.value));if(!t||!n||!o.length)return};$pizzaForm.addEventListener("submit",handlePizzaSubmit),$addToppingBtn.addEventListener("click",handleAddTopping);