const $pizzaList=document.querySelector("#pizza-list"),printPizza=({_id:n,pizzaName:a,toppings:s,size:i,commentCount:e,createdBy:t,createdAt:c})=>{const l=`\n    <div class="col-12 col-lg-6 flex-row">\n      <div class="card w-100 flex-column">\n        <h3 class="card-header">${a}</h3>\n        <div class="card-body flex-column col-auto">\n          <h4 class="text-dark">By ${t}</h4>\n          <p>On ${c}</p>\n          <p>${e} Comments</p>\n          <h5 class="text-dark">Suggested Size: ${i}\n          <h5 class="text-dark">Toppings</h5>\n          <ul>\n            ${s.map(n=>`<li>${n}</li>`).join("")}\n          </ul>\n          <a class="btn display-block w-100 mt-auto" href="/pizza?id=${n}">See the discussion.</a>\n        </div>\n      </div>\n    </div>\n  `;$pizzaList.innerHTML+=l};